package com.capacitorjs.portals;

public class PortalApp {

    private String name;
    private String id;
    private String webDir;
    private LiveUpdateConfig liveUpdateConfig;

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getId() {
        return id;
    }

    public void setId(String id) {
        this.id = id;
    }

    public String getWebDir() {
        return webDir;
    }

    public void setWebDir(String webDir) {
        this.webDir = webDir;
    }

    public LiveUpdateConfig getLiveUpdateConfig() {
        return liveUpdateConfig;
    }

    public void setLiveUpdateConfig(LiveUpdateConfig liveUpdateConfig) {
        this.liveUpdateConfig = liveUpdateConfig;
    }
}
