import Foundation

struct Portal {
    var name: String
    var updatedPath: String?
    var liveUpdateConfig: LiveUpdateConfig?
}

struct LiveUpdateConfig: Decodable {
    enum AutoUpdateMethod: String, Decodable {
        case none, background
    }

    var appId: String
    var channel: String
    var autoUpdateMethod: AutoUpdateMethod
    var maxVersions: Int?
}

extension Portal: Decodable {}
